mapper.select:
select
  m_id, m_face_img, m_grade, m_item_id, m_job, m_name, m_number, m_skill, m_status, create_time, create_user, update_time, update_user, deleted, version
from team01.javeee_member


        and m_id = #{mId}


        and m_face_img = #{mFaceImg}


        and m_grade = #{mGrade}


        and m_item_id = #{mItemId}


        and m_job = #{mJob}


        and m_name = #{mName}


        and m_number = #{mNumber}


        and m_skill = #{mSkill}


        and m_status = #{mStatus}


        and create_time = #{createTime}


        and create_user = #{createUser}


        and update_time = #{updateTime}


        and update_user = #{updateUser}


        and deleted = #{deleted}


        and version = #{version}
